import React, { useEffect, useState } from 'react'
import axios from 'axios'
import Loader from '../component/Loader'
import ProductCard from '../component/ProductCard'

function ProductService() {
    const url = `https://fakestoreapi.com/products`
    
    let content = null



    const [product, setProduct] = useState({
        loading: false,
        data: null,
        error: false
    })

    function getProducts(){
        const [products, setProducts] = this.useState({
            loading: false,
            data: null,
            error: false
        })

        this.useEffect(() => {
            setProducts({
                loading: true,
                data: null,
                error: false
            })
            axios.get(url).then(response => {
                setProducts({
                    loading: false,
                    data: response.data,
                })
            })
            .catch(() => {
                setProducts({
                    loading: false,
                    data: null,
                    error:true
                })
            })
        }, [url])

        if(products.error){
            content = <div>
                Please try again.
            </div>
        }
     
        if(products.loading) {
            content = <div>
                <Loader></Loader>
            </div>
        }
    
        if(products.data){
            console.log(products.data)
            content = products.data.map((product, key) =>
                <div key={product.id}>
                    <ProductCard 
                        product={product} 
                    />
                </div>
            )
        }
        return (
            this.content = content
        )
    }


    
    

    // let content = null

    

    return (
        <div>
            <h1 className="font-bold text-xl p-3">Products</h1>
            content = {content}
        </div>
    )
}

export default ProductService
