import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {useParams} from 'react-router-dom'
import Loader from '../component/Loader'
import ProductCard from '../component/ProductCard'

function Product() {
    const {id} = useParams()

    const url = `https://fakestoreapi.com/products/${id}`
    const [product, setProduct] = useState({
        loading: false,
        data: null,
        error: false
    })
    let content = null

    useEffect(() => {
        setProduct({
            loading: true,
            data: null,
            error: false
        })
        axios.get(url).then(response => {
            setProduct({
                loading: false,
                data: response.data,
            })
        })
        .catch(() => {
            setProduct({
                loading: false,
                data: null,
                error:true
            })
        })
    }, [url])

    if(product.error){
        content = <p>
            Item not found.
        </p>
    }
 
    if(product.loading) {
        content = <p>
            <Loader></Loader>
        </p>
    }

    if(product.data){
        console.log(product.data)
        content = 
        product.data.map((product, key) =>
        <span key={product.id}>
                         <ProductCard 
                            product={product} 
                        />       
        </span>
        )}
    
