//                 {product.data.data.email}
//             </div>
//             <div className="text-sm w-40">
//                 {product.data.ad.text}
//             </div>
//         </div>
        // <div>
        //     <img src={product.ThumbnailUrl}
        //     alt src={product.title}></img>

        // </div>
//    }
   return (
       <div>
           {content}
       </div>
   )
}

export default Product
