import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {useParams} from 'react-router-dom'
import Loader from '../component/Loader'

function Home() {
    const {id} = useParams()

    const url = `https://fakestoreapi.com/products`
    const [products, setProducts] = useState({
        loading: false,
        data: null,
        error: false
    })
    let content = null

    useEffect(() => {
        setProducts({
            loading: true,
            data: null,
            error: false
        })
        axios.get(url).then(response => {
            setProducts({
                loading: false,
                data: response.data,
            })
        })
        .catch(() => {
            setProducts({
                loading: false,
                data: null,
                error:true
            })
        })
    }, [url])

    if(products.error){
        content = <div>
            Please try again.
        </div>
    }
 
    if(products.loading) {
        content = <div>
            <Loader></Loader>
        </div>
    }
   if(products.data){
       console.log(products.data)
       content = 
        <div>
            <h1 className="font-bold text-xl w-40 mb-3">
                {products.data.title}
            </h1>
            <div>
                <img 
                    src={products.data.image}
                    alt={products.data.title}
                    />
            </div>
            <div className="text-sm w-40 mb-2">
                Category: {products.data.category}
            </div>
            <div className="text-sm w-40 mb-2">
                Product id: {products.data.id}
            </div>
            <div className="text-sm w-40 mb2">
                Description: {products.data.description}
            </div>
            <div className="text-sm w-40 mb-2">
                Price: ${products.data.price}
            </div>
          
        </div>
        
   }

    


    return (
        <div>
            <h1 className="font-bold text-xl p-3">Products</h1>

            {content}
        </div>
    )
}

export default Home
