import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {useParams} from 'react-router-dom'

function Home() {
    const {id} = useParams()

    const url = `https://fakestoreapi.com/products`
    const [product, setProduct] = useState({
        loading: false,
        data: null,
        error: false
    })
    let content = null

    useEffect(() => {
        setProduct({
            loading: true,
            data: null,
            error: false
        })
        axios.get(url).then(response => {
            setProduct({
                loading: false,
                data: response.data,
            })
        })
        .catch(() => {
            setProduct({
                loading: false,
                data: null,
                error:true
            })
        })
    }, [url])


    return (
        <div>
            <h1 className="font-bold text-xl p-3">Products</h1>

            {content}
        </div>
    )
}

export default Home
