import React, { useEffect, useState } from 'react'
import axios from 'axios'

export function useAxiosPost(url) {
    
    // let user = axios.p
    const [request, setRequest] = useState({
        loading: false,
        data: null,
        error: false
    })
    
    useEffect(() => {
        setRequest({
            loading: true,
            data: null,
            error: false
        })
        axios.post(url).then(response => {
            setRequest({
                loading: false,
                data: response.data,
            })
        })
        .catch(() => {
            setRequest({
                loading: false,
                data: null,
                error:true
            })
        })
    }, [url])

    return request
}
