import React from 'react'
import { useAxiosPost } from '../Hooks/PostRequest';
import Loader from '../component/Loader';
import RegristrationCard from '../component/RegristrationCard';


    
// let user = useAxiosPost(url)
let url = `https://5f46c004aaaf9a00161510ef.mockapi.io/api/v1/users`
// let content = null

export default class RegristrationForm extends React.Component {

    

    state = {
        firstName: '',
        lastName: '',
        email: '',
        username: '',
        password: ''
    }

    change = e => {
        // this.props.onChange({ [e.target.name]: e.target.value})
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    onSubmit = (e) => {
        e.preventDefault();
        // this.props.onSubmit()
        this.setState({
            firstName: "",
            lastName: "",
            email: "",
            username: "",
            password: ""
        })
        console.log(this.state)
    }

    render() {
        return(
            <form>
                <input 
                name="firstName"
                placeholder="First Name" 
                value={this.state.firstName} 
                onChange={e => this.change(e)} 
                />
                <input 
                name="lastName"
                placeholder="Last Name" 
                value={this.state.lastName} 
                onChange={e => this.change(e)} 
                />
                <input 
                name="email"
                placeholder="Email" 
                value={this.state.email} 
                onChange={e => this.change(e)} 
                />
                <input 
                name="username"
                placeholder="Username" 
                value={this.state.username} 
                onChange={e => this.change(e)} 
                />
                <input 
                name="password"
                placeholder="Password" 
                type="password"
                value={this.state.password} 
                onChange={e => this.change(e)} 
                />
                <button onClick={e => this.onSubmit(e)}>Submit</button>
            </form>
        )
    }
}
