                    {props.product.title}
                </div>
                <div className="text-sm w-50">
                    {props.product.category} 
                    <p>{props.product.description}</p>
                </div>
            {props.product.price}
        </div>
            
        </div> */}
     </div>
    )
}

export default ProductCard
