import React from 'react'
import { useAxiosPost } from '../Hooks/PostRequest';
import Loader from '../component/Loader';
import RegristrationCard from '../component/RegristrationCard';


    
// let user = useAxiosPost(url)

// let content = null

export default function RegristrationForm() {

    let url = `https://5f46c004aaaf9a00161510ef.mockapi.io/api/v1/users`

    // e.preventDefault()
    
    this.setState({
       firstName: '',
       lastName: '',
       email: '',
       username: '',
       password: ''
    })

    let user = useAxiosPost(url)

    let content = null

    
        
   //  console.log(user)
   //  if(user){
   //      content = user.data
   //      console.log(content)
   //  }
    console.log(this.state)
    console.log(user)


    

    if(user.error){
        content = <div>
            Please try again.
        </div>
    }
 
    if(user.loading) {
        content = <div>
            <Loader></Loader>
        </div>
    }

    if(user.data){
        content = user.data.map((user, key) =>
            <div key={user.id}>
                <RegristrationCard 
                    user={user} 
                />
            </div>
        )
    }
        return (
            <div>
                <h1 className="font-bold text-xl p-3">Products</h1>
                {content}
            </div>
        )

         
}
