import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {useParams} from 'react-router-dom'
import Loader from '../component/Loader'

function Product() {
    const {id} = useParams()

    const url = `https://reqres.in/api/users/${id}`
    const [product, setProduct] = useState({
        loading: false,
        data: null
    })
    let content = null

    useEffect(() => {
        setProduct({
            loading: true,
            data: null
        })
        axios.get(url).then(response => {
            setProduct({
                loading: false,
                data: response.data
            })
        })
    }, [url])
 
    if(product.loading) {
        content = <p>
            <Loader></Loader>
        </p>
    }
   if(product.data){
       content = 
        <div>
            <h1 className="font-bold text-xl w-40 mb-3">
                {product.data.data.first_name + " " + product.data.data.last_name}
            </h1>
            <div>
                <img 
                    src={product.data.data.avatar}
                    alt={product.data.data.name}
                    />
            </div>
            <div className="text-sm w-40 mb-2">
                {product.data.data.email}
            </div>
            <div className="text-sm w-40">
                {product.data.ad.text}
            </div>
        </div>
        // <div>
        //     <img src={product.ThumbnailUrl}
        //     alt src={product.title}></img>

        // </div>
   }

   return (
       <div>
           {content}
       </div>
   )
}

export default Product
