import React from 'react'
import { Link } from 'react-router-dom'

export default function RegristrationCard(props) {

    let state = {
        firstName: '',
        lastName: '',
        email: '',
        username: '',
        password: ''
    }

    let change = e => {
        const {name, value} = e.target;
        e.preventDefault();
        console.log(e.target.value)
        console.log(e.target.name)
        console.log(state)
        // console.log(this.state)
        // this.props.onChange({ [e.target.name]: e.target.value})
        this.setState({
            [name]: value
            // [e.target.name]: e.target.value
        });
    }

    fetch('https://5f46c004aaaf9a00161510ef.mockapi.io/api/v1/users', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            state
        })
    })
    
    return(<div>
        <h1 className="font-bold text-xl p-3 flex items-center justify-center">Sign Up</h1>
        <form>
            <div className="w-50">
               <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50mt-2">
                   <input 
                       type="text" 
                       name="firstName" 
                       placeholder="First Name"
                       value={state.firstName}
                       onChange={e => this.change(e)}
                   />
               </div>
               <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50 mt-2">
                   <input 
                       name="lastName"
                       placeholder="Last Name" 
                       value={state.lastName} 
                       onChange={e => change(e)} 
                   />
               </div>
               <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50 mt-2">
                   <input 
                       name="email"
                       placeholder="Email" 
                       value={state.email} 
                       onChange={e => change(e)} 
                   />
               </div>
               <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50 mt-2">
                   <input 
                       name="username"
                       placeholder="Username" 
                       value={state.username} 
                       onChange={e => change(e)} 
                   />
               </div>
               <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50 mt-2">
                   <input 
                       name="password"
                       placeholder="Password" 
                       type="password"
                       value={state.password} 
                       onChange={e => change(e)} 
                   />
               </div>
           </div >
           <div className="p-3">
           <button className="p-3 bg-gray-500 rounded mt-2 shadow-lg block mr-20 ml-20 w-50mt-2 w-40 border-1px-solic-black" 
           onClick={e => this.fetch()}>Sign Up</button>
           </div>
        </form>
        </div>
    )
    
}

export class Register extends React.Component {

    state = {
        firstName: '',
        lastName: '',
        email: '',
        username: '',
        password: ''
    }

}
