import React, { useEffect, useState } from 'react'
import Axios from 'axios'

export function useAxiosGet(url) {

    const [request, setResponse] = useState({
        loading: false,
        data: null,
        error: false
    })
    
    useEffect(() => {
        setResponse({
            loading: true,
            data: null,
            error: false
        })
        Axios.get(url).then(response => {
            setResponse({
                loading: false,
                data: response.data,
            })
        })
        .catch(() => {
            setResponse({
                loading: false,
                data: null,
                error:true
            })
        })
    }, [url])

    return request
}
