import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {useParams} from 'react-router-dom'
import Loader from '../component/Loader'

function Home() {
    const {id} = useParams()

    const url = `https://fakestoreapi.com/products`
    const [product, setProduct] = useState({
        loading: false,
        data: null,
        error: false
    })
    let content = null

    useEffect(() => {
        setProduct({
            loading: true,
            data: null,
            error: false
        })
        axios.get(url).then(response => {
            setProduct({
                loading: false,
                data: response.data,
            })
        })
        .catch(() => {
            setProduct({
                loading: false,
                data: null,
                error:true
            })
        })
    }, [url])

    if(product.error){
        content = <div>
            Please try again.
        </div>
    }
 
    if(product.loading) {
        content = <div>
            <Loader></Loader>
        </div>
    }
   if(product.data){
       for(let i=0; i<10; i++){
        console.log(product.data)
        content = 
         <div>
             <h1 className="font-bold text-xl w-40 mb-3">
                 {product.data.title}
             </h1>
             <div>
                 <img 
                     src={product.data.image}
                     alt={product.data.title}
                     />
             </div>
             <div className="text-sm w-40 mb-2">
                 Category: {product.data.category}
             </div>
             <div className="text-sm w-40 mb-2">
                 Product id: {product.data.id}
             </div>
             <div className="text-sm w-40 mb2">
                 Description: {product.data.description}
             </div>
             <div className="text-sm w-40 mb-2">
                 Price: ${product.data.price}
             </div>
           
         </div>
       }
       
   }

    


    return (
        <div>
            <h1 className="font-bold text-xl p-3">Products</h1>

            {content}
        </div>
    )
}

export default Home
