import React from 'react'

export default class RegristrationForm extends React.Component {

     state = {
         firstName: '',
         lastName: '',
         email: '',
         username: '',
         password: ''
     }

     change = e => {
         this.setState({
             [e.target.name]: e.target.value
         })
     }

     render() {
         return(
             <form>
                 <input 
                 type="text" 
                 name="firstName" 
                 placeholder="First Name"
                 value={this.state.firstName}
                 onChange={e => this.change(e)}
                 />
             </form>
         )
     }
}
