import React from 'react'

export default class RegristrationForm extends React.Component {

     state = {
         firstName: '',
         lastName: '',
         email: '',
         username: '',
         password: ''
     }

     change = e => {
         this.setState({
             [e.target.name]: e.target.value
         })
     }

     onSubmit = (e) => {
         e.preventDefault()
         this.setState({
            firstName: '',
            lastName: '',
            email: '',
            username: '',
            password: ''
         })
         console.log(this.state)

     }

     render() {
         return(
             <form>
                 <div className="w-50">
                    <div className="p-3 shadow-lg block mr-20 ml-20 w-50mt-2">
                        <input 
                            type="text" 
                            name="firstName" 
                            placeholder="First Name"
                            value={this.state.firstName}
                            onChange={e => this.change(e)}
                        />
                    </div>
                    <div className="p-3 shadow-lg block mr-20 ml-20 w-50 mt-2">
                        <input 
                            name="lastName"
                            placeholder="Last Name" 
                            value={this.state.lastName} 
                            onChange={e => this.change(e)} 
                        />
                    </div>
                    <div className="p-3 shadow-lg block mr-20 ml-20 w-50 mt-2">
                        <input 
                            name="email"
                            placeholder="Email" 
                            value={this.state.email} 
                            onChange={e => this.change(e)} 
                        />
                    </div>
                    <div className="p-3 shadow-lg block mr-20 ml-20 w-50 mt-2">
                        <input 
                            name="username"
                            placeholder="Username" 
                            value={this.state.username} 
                            onChange={e => this.change(e)} 
                        />
                    </div>
                    <div className="p-3 shadow-lg block mr-20 ml-20 w-50 mt-2">
                        <input 
                            name="password"
                            placeholder="Password" 
                            type="password"
                            value={this.state.password} 
                            onChange={e => this.change(e)} 
                        />
                    </div>
                </div >
                <div className="p-3">
                <button className="p-46 bg-gray-500 rounded shadow-lg block mr-20 ml-20 w-50 mt-2 w-40 border-1px-solic-black" 
                onClick={e => this.onSubmit(e)}>Sign Up</button>
                </div>
             </form>
         )
     }
}
