import React from 'react'
import { useAxiosPost } from '../Hooks/PostRequest';


    
// let user = useAxiosPost(url)

// let content = null

export default class RegristrationForm extends React.Component {


    
    // constructor(props) {
    //     super(props);

    //     this.state = {
    //         firstName: null,
    //         lastName: null,
    //         email: null,
    //         password: null,
    //     }
  
    // }
    


     state = {
         firstName: '',
         lastName: '',
         email: '',
         username: '',
         password: ''
     }

     change = e => {
         this.setState({
             [e.target.name]: e.target.value
         })
     }

    //  onSubmit = (e) => {
    //     let url = `https://5f46c004aaaf9a00161510ef.mockapi.io/api/v1/users`
        
    //      e.preventDefault()
    //      let user = useAxiosPost(url)
    //      this.setState({
    //         firstName: '',
    //         lastName: '',
    //         email: '',
    //         username: '',
    //         password: ''
    //      })
    //     //  console.log(user)
    //     //  if(user){
    //     //      content = user.data
    //     //      console.log(content)
    //     //  }
    //      console.log(this.state)
    //      console.log(user)

    //  }

     render() {
         return(<div>
             <h1 className="font-bold text-xl p-3 flex items-center justify-center">Sign Up</h1>
             <form>
                 <div className="w-50">
                    <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50mt-2">
                        <input 
                            type="text" 
                            name="firstName" 
                            placeholder="First Name"
                            value={this.state.firstName}
                            onChange={e => this.change(e)}
                        />
                    </div>
                    <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50 mt-2">
                        <input 
                            name="lastName"
                            placeholder="Last Name" 
                            value={this.state.lastName} 
                            onChange={e => this.change(e)} 
                        />
                    </div>
                    <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50 mt-2">
                        <input 
                            name="email"
                            placeholder="Email" 
                            value={this.state.email} 
                            onChange={e => this.change(e)} 
                        />
                    </div>
                    <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50 mt-2">
                        <input 
                            name="username"
                            placeholder="Username" 
                            value={this.state.username} 
                            onChange={e => this.change(e)} 
                        />
                    </div>
                    <div className="p-3 shadow-lg inline-block mr-20 ml-20 w-50 mt-2">
                        <input 
                            name="password"
                            placeholder="Password" 
                            type="password"
                            value={this.state.password} 
                            onChange={e => this.change(e)} 
                        />
                    </div>
                </div >
                <div className="p-3">
                <button className="p-3 bg-gray-500 rounded mt-2 shadow-lg block mr-20 ml-20 w-50mt-2 w-40 border-1px-solic-black" 
                onClick={e => this.onSubmit(e)}>Sign Up</button>
                </div>
             </form>
             </div>
         )
     }
}

export function onSubmit() {

    let url = `https://5f46c004aaaf9a00161510ef.mockapi.io/api/v1/users`
        
    e.preventDefault()
    let user = useAxiosPost(url)
    this.setState({
       firstName: '',
       lastName: '',
       email: '',
       username: '',
       password: ''
    })
   //  console.log(user)
   //  if(user){
   //      content = user.data
   //      console.log(content)
   //  }
    console.log(this.state)
    console.log(user)

}
